package chapter2;

public class chap2_p78 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10,y=20;
		System.out.println(" "+(x==y));
		System.out.println(" "+(x!=y));
		System.out.println(" "+(x>y));
		System.out.println(" "+(x<y));
		System.out.println(" "+(x>=y));
		System.out.println(" "+(x<=y));
	}

}
